mapper.select:
SELECT QTY AS value
FROM INVENTORY
WHERE ITEMID = #{itemId}

mapper.update:
UPDATE ITEM
SET ISAD = #{param2}
WHERE ITEMID = #{param1}

mapper.select:
SELECT ITEMID, SELLER, UNITCOST, ATTR1, ATTR2, ATTR3, ATTR4, ATTR5 FROM PRODUCT, ITEM WHERE PRODUCT.PRODUCTID = ITEM.PRODUCTID
AND lower(name) like #{keywords}
AND lower(seller) like #{keywords}

mapper.update:
UPDATE INVENTORY 
SET QTY = QTY - #{increment}
WHERE ITEMID = #{itemId}
